public class CCameraManager : CSingleton<CCameraManager>
{
		public enum ECameraNum
		{
			NO_CURRENT_CAMERA,
			DEFAULT_PERSPECTIVE_CAMERA,
			DEFAULT_ORTHO_CAMERA,
			CAMERA_MAX = 255
		}

		public CCameraManager()
		{
			this.m_pCurrentCamera = null;
			this.m_pPreviousCamera = null;
			AddCamera(DEFAULT_PERSPECTIVE_CAMERA);
			AddCamera(DEFAULT_ORTHO_CAMERA);

			SetCurrentCamera(DEFAULT_PERSPECTIVE_CAMERA);
		}

		public virtual void Dispose()
		{
			for (SortedDictionary<byte, CCamera>.Enumerator itor = m_CameraMap.begin(); itor.MoveNext();)
			{
				deleteitor.Current.Value;
			}
			m_CameraMap.clear();
		}

		public bool AddCamera(byte ucCameraNum)
		{
			if (m_CameraMap.end() != m_CameraMap.find(ucCameraNum))
			{
				return false;
			}
			m_CameraMap.insert(SortedDictionary<byte, CCamera>.value_type(ucCameraNum, new CCamera()));
			return true;
		}

		public bool RemoveCamera(byte ucCameraNum)
		{
			SortedDictionary<byte, CCamera >.Enumerator itor = m_CameraMap.find(ucCameraNum);
			if (m_CameraMap.end() == itor)
			{
				return false;
			}
			m_CameraMap.erase(itor);
			return true;
		}

		public CCamera GetCurrentCamera()
		{
			if (!m_pCurrentCamera)
			{
				Debug.Assert(false);
			}
			return m_pCurrentCamera;
		}

		public void SetCurrentCamera(byte ucCameraNum)
		{
			if (m_pCurrentCamera != m_CameraMap[ucCameraNum])
			{
				m_pPreviousCamera = m_pCurrentCamera;
			}

			m_pCurrentCamera = m_CameraMap[ucCameraNum];
		}

		public void ResetToPreviousCamera()
		{
			if (!m_pPreviousCamera)
			{
				Debug.Assert(false);
			}
			m_pCurrentCamera = m_pPreviousCamera;
			m_pPreviousCamera = null;
		}

		public bool isCurrentCamera(byte ucCameraNum)
		{
			if (m_CameraMap[ucCameraNum] == m_pCurrentCamera)
			{
				return true;
			}
			return false;
		}

		public byte GetCurrentCameraNum()
		{
			if (!m_pCurrentCamera)
			{
				return NO_CURRENT_CAMERA;
			}
			for (SortedDictionary<byte, CCamera>.Enumerator itor = m_CameraMap.begin(); itor.MoveNext();)
			{
				if (m_pCurrentCamera == itor.Current.Value)
				{
					return itor.Current.Key;
				}
			}
			return NO_CURRENT_CAMERA;
		}

		public bool isTerrainCollisionEnable()
		{
			return m_pCurrentCamera.isTerrainCollisionEnable();
		}

		public void SetTerrainCollision(bool bEnable)
		{
			m_pCurrentCamera.SetTerrainCollision(bEnable);
		}

		private SortedDictionary<byte, CCamera > m_CameraMap = new SortedDictionary<byte, CCamera >();
		private CCamera m_pCurrentCamera;
		private CCamera m_pPreviousCamera;
}