				if (!GetPickedSlotPointer(pSlot))
				{
					UI.CWindowManager.Instance().DeattachIcon();
					return true;
				}

				if (pSlot.isItem && (pSlot.dwState & SLOT_STATE_LOCK) == 0)
				{
					OnSelectItemSlot(pSlot.dwSlotNumber);
