			public void Update()
			{
				if (!IsShow())
				{
					return;
				}

				__RemoveReserveChildren();

				OnUpdate();

				m_isUpdatingChildren = true;
				TWindowContainer.iterator it = new TWindowContainer.iterator();
				for (it = m_pChildList.begin(); it != m_pChildList.end();)
				{
					TWindowContainer.iterator it_next = it;
					++it_next;
					it.Update();
//# Laniatus Games Studio Inc. | TODO TASK: The following line was determined to be a copy assignment (rather than a reference assignment) - this should be verified and a 'CopyFrom' method should be created:
//Original Metin2 CPlus Line: it = it_next;
					it.CopyFrom(it_next);
				}
				m_isUpdatingChildren = false;
			}

			public void Render()
			{
