					CPythonGraphic.Instance().RenderBox2d(m_rect.left, m_rect.top, m_rect.right, m_rect.bottom);
				}
#endif

				std::for_each(m_pChildList.begin(), m_pChildList.end(), std::mem_fn(Render));
			}

