			{
				if (m_pParent)
				{
					if (m_pParent.IsWindow())
					{
						return m_pParent.GetRoot();
					}
				}

				return this;
			}

			public CWindow GetParent()
			{
				return m_pParent;
			}

			public bool IsChild(CWindow pWin)
			{
				LinkedList<CWindow >.Enumerator itor = m_pChildList.begin();

				while (itor.MoveNext())
				{
					if (itor.Current == pWin)
					{
						return true;
					}

				}

				return false;
			}

			public void DeleteChild(CWindow pWin)
			{
				if (m_isUpdatingChildren)
				{
					m_pReserveChildList.push_back(pWin);
				}
				else
				{
					m_pChildList.remove(pWin);
				}
			}

			public void SetTop(CWindow pWin)
			{
				if (!pWin.IsFlag(CWindow.FLAG_FLOAT))
				{
					return;
				}

				TWindowContainer.iterator itor = std::find(m_pChildList.begin(), m_pChildList.end(), pWin);
				if (m_pChildList.end() != itor)
				{
					m_pChildList.push_back(*itor);
					m_pChildList.erase(itor);

					pWin.OnTop();
				}
				else
				{
					TraceError(" CWindow::SetTop - Failed to find child window\n");
				}
			}

			public bool IsIn(int x, int y)
			{
				if (x >= m_rect.left && x <= m_rect.right)
				{
					if (y >= m_rect.top && y <= m_rect.bottom)
					{
						return true;
					}
				}

				return false;
			}

			public bool IsIn()
			{
				int lx;
				int ly;
				UI.CWindowManager.Instance().GetMousePosition(lx, ly);

				return IsIn(lx, ly);
			}

			public CWindow PickWindow(int x, int y)
			{
				LinkedList<CWindow >.reverse_iterator ritor = m_pChildList.rbegin();
				for (; ritor != m_pChildList.rend(); ++ritor)
				{
					CWindow pWin = *ritor;
					if (pWin.IsShow())
					{
						if (!pWin.IsFlag(CWindow.FLAG_IGNORE_SIZE))
						{
							if (!pWin.IsIn(x, y))
							{
								if (0 <= pWin.GetWidth())
								{
									continue;
								}
							}
						}

						CWindow pResult = pWin.PickWindow(x, y);
						if (pResult != null)
						{
							return pResult;
						}
					}
				}

				if (IsFlag(CWindow.FLAG_NOT_PICK))
				{
					return null;
				}

				return (this);
			}

			public CWindow PickTopWindow(int x, int y)
			{
				LinkedList<CWindow >.reverse_iterator ritor = m_pChildList.rbegin();
				for (; ritor != m_pChildList.rend(); ++ritor)
				{
					CWindow pWin = *ritor;
					if (pWin.IsShow())
					{
						if (pWin.IsIn(x, y))
						{
							if (!pWin.IsFlag(CWindow.FLAG_NOT_PICK))
							{
								return pWin;
							}
						}
					}
				}

				return null;
			}

			public void __RemoveReserveChildren()
			{
				if (m_pReserveChildList.empty())
				{
					return;
				}

				TWindowContainer.iterator it = new TWindowContainer.iterator();
				for (it = m_pReserveChildList.begin(); it != m_pReserveChildList.end(); ++it)
				{
					m_pChildList.remove(*it);
				}
				m_pReserveChildList.clear();
			}

			public void AddFlag(uint flag)
			{
				((m_dwFlag) |= (flag));
			}
			public void RemoveFlag(uint flag)
			{
