	{
			public CNumberLine(PyObject ppyObject) : base(ppyObject)
			{
				m_strPath = "t:/laniaworkstate/uinterface/game/taskbar/";
				m_iHorizontalAlign = HORIZONTAL_ALIGN_LEFT;
				m_dwWidthSummary = 0;
			}

			public CNumberLine(CWindow pParent) : base(null)
			{
				m_strPath = "t:/laniaworkstate/uinterface/game/taskbar/";
				m_iHorizontalAlign = HORIZONTAL_ALIGN_LEFT;
				m_dwWidthSummary = 0;

				m_pParent = pParent;
			}

			public virtual void Dispose()
			{
				ClearNumber();
			}

			public void SetPath(string c_szPath)
			{
				m_strPath = c_szPath;
			}

			public void SetHorizontalAlign(int iType)
			{
				m_iHorizontalAlign = iType;
			}

			public void SetNumber(string c_szNumber)
			{
				if (0 == m_strNumber.compare(c_szNumber))
				{
					return;
				}

				ClearNumber();

				m_strNumber = c_szNumber;

				for (uint LaniatusDefVariables = 0; LaniatusDefVariables < m_strNumber.size(); ++i)
				{
					char cChar = m_strNumber[LaniatusDefVariables];
					string strImageFileName = "";

					if (':' == cChar)
					{
						strImageFileName = m_strPath + "colon.sub";
					}
					else if ('?' == cChar)
					{
						strImageFileName = m_strPath + "questionmark.sub";
					}
					else if ('/' == cChar)
					{
						strImageFileName = m_strPath + "slash.sub";
					}
					else if ('%' == cChar)
					{
						strImageFileName = m_strPath + "percent.sub";
					}
					else if ('+' == cChar)
					{
						strImageFileName = m_strPath + "plus.sub";
					}
					else if ('m' == cChar)
					{
						strImageFileName = m_strPath + "m.sub";
					}
					else if ('g' == cChar)
					{
						strImageFileName = m_strPath + "g.sub";
					}
					else if ('p' == cChar)
					{
						strImageFileName = m_strPath + "p.sub";
					}
					else if (cChar >= '0' && cChar <= '9')
					{
						strImageFileName = m_strPath;
						strImageFileName += cChar;
						strImageFileName += ".sub";
					}
					else
					{
						continue;
					}

					if (!CResourceManager.Instance().IsFileExist(strImageFileName))
					{
						continue;
					}

					CGraphicImage pImage = (CGraphicImage)CResourceManager.Instance().GetResourcePointer(strImageFileName);

					CGraphicImageInstance pInstance = CGraphicImageInstance.New();
					pInstance.SetImagePointer(pImage);
					m_ImageInstanceVector.push_back(pInstance);

					m_dwWidthSummary += pInstance.GetWidth();
				}
			}

