			m_fAtkSpd = fAtkSpd;
		}

		public void SetMoveSpeed(float fMovSpd)
		{
			if (m_fMovSpd == fMovSpd)
			{
				return;
			}

			m_fMovSpd = fMovSpd;

			if (__IsMoveMotion())
			{
				Stop();
				Move();
			}
		}

