		public bool IntersectDefendingSphere()
		{
			for (TCollisionPointInstanceList.iterator it = m_DefendingPointInstanceList.begin(); it != m_DefendingPointInstanceList.end(); ++it)
			{
				List<CDynamicSphereInstance> rSphereInstanceVector = it.SphereInstanceVector;

				List<CDynamicSphereInstance>.Enumerator it2 = rSphereInstanceVector.GetEnumerator();
				while (it2.MoveNext())
				{
					CDynamicSphereInstance rInstance = it2.Current;
					_D3DVECTOR v3SpherePosition = rInstance.v3Position;
					float fRadius = rInstance.fRadius;

					_D3DVECTOR v3Orig = new _D3DVECTOR();
					_D3DVECTOR v3Dir = new _D3DVECTOR();
					float fRange;
					ms_Ray.GetStartPoint(v3Orig);
					ms_Ray.GetDirection(v3Dir, fRange);

					_D3DVECTOR v3Distance = v3Orig - v3SpherePosition;
					float b = D3DXVec3Dot(v3Dir, v3Distance);
					float c = D3DXVec3Dot(v3Distance, v3Distance) - fRadius * fRadius;

					if (b * b - c >= 0F)
					{
						return true;
					}
				}
			}
			return false;
		}

