							}
						}
						else
						{
							m_isGoing = false;

							if (!IsWaiting())
							{
								EndWalking();
							}

							SCRIPT_SetPixelPosition(kPPosDst.x, kPPosDst.y);
							SetAdvancingRotation(fRotDst);
							SetRotation(fRotDst);
						}
						break;
					}

					case FUNC_MOVE:
					{
						NEW_SetSrcPixelPosition(kPPosCur);
						NEW_SetDstPixelPosition(kPPosDst);
						m_fDstRot = fRotDst;
						m_isGoing = true;
						__EnableSkipCollision();

						m_kMovAfterFunc.eFunc = FUNC_MOVE;

						if (!IsWalking())
						{
							StartWalking();
						}
						else
						{
						}
						break;
					}

					case FUNC_COMBO:
					{
						if (fDirLen >= 50.0f)
						{
							NEW_SetSrcPixelPosition(kPPosCur);
							NEW_SetDstPixelPosition(kPPosDst);
							m_fDstRot = fRotDst;
							m_isGoing = true;
							__EnableSkipCollision();

							m_kMovAfterFunc.eFunc = FUNC_COMBO;
							m_kMovAfterFunc.uArg = uArg;

							if (!IsWalking())
							{
								StartWalking();
							}
						}
						else
						{
							m_isGoing = false;

							if (IsWalking())
							{
								EndWalking();
							}

							SCRIPT_SetPixelPosition(kPPosDst.x, kPPosDst.y);
							RunComboAttack(fRotDst, uArg);
						}
						break;
					}

					case FUNC_ATTACK:
					{
						if (fDirLen >= 50.0f)
						{
							NEW_SetSrcPixelPosition(kPPosCur);
							NEW_SetDstPixelPosition(kPPosDst);
							m_fDstRot = fRotDst;
							m_isGoing = true;
							__EnableSkipCollision();

							m_kMovAfterFunc.eFunc = FUNC_ATTACK;

							if (!IsWalking())
							{
								StartWalking();
							}
						}
						else
						{
							m_isGoing = false;

							if (IsWalking())
							{
								EndWalking();
							}

							SCRIPT_SetPixelPosition(kPPosDst.x, kPPosDst.y);
							BlendRotation(fRotDst);

							RunNormalAttack(fRotDst);
						}
						break;
					}

					case FUNC_MOB_SKILL:
					{
						if (fDirLen >= 50.0f)
						{
							NEW_SetSrcPixelPosition(kPPosCur);
							NEW_SetDstPixelPosition(kPPosDst);
							m_fDstRot = fRotDst;
							m_isGoing = true;
							__EnableSkipCollision();

							m_kMovAfterFunc.eFunc = FUNC_MOB_SKILL;
							m_kMovAfterFunc.uArg = uArg;

							if (!IsWalking())
							{
								StartWalking();
							}
						}
						else
						{
							m_isGoing = false;

							if (IsWalking())
							{
								EndWalking();
							}

							SCRIPT_SetPixelPosition(kPPosDst.x, kPPosDst.y);
							BlendRotation(fRotDst);

							m_GraphicThingInstance.InterceptOnceMotion(CRaceMotionData.NAME_SPECIAL_1 + uArg);
						}
						break;
					}

					case FUNC_EMOTION:
					{
						if (fDirLen > 100.0f)
						{
							NEW_SetSrcPixelPosition(kPPosCur);
							NEW_SetDstPixelPosition(kPPosDst);
							m_fDstRot = fRotDst;
							m_isGoing = true;

							if (__IsMainInstance())
							{
								__EnableSkipCollision();
							}

							m_kMovAfterFunc.eFunc = FUNC_EMOTION;
							m_kMovAfterFunc.uArg = uArg;
							m_kMovAfterFunc.uArgExpanded = uTargetVID;
							m_kMovAfterFunc.kPosDst = kPPosDst;

							if (!IsWalking())
							{
								StartWalking();
							}
						}
						else
						{
							__ProcessFunctionEmotion(uArg, uTargetVID, kPPosDst);
						}
						break;
					}

					default:
					{
						if ((eFunc & FUNC_SKILL) != 0)
						{
							if (fDirLen >= 50.0f)
							{
								NEW_SetSrcPixelPosition(kPPosCur);
								NEW_SetDstPixelPosition(kPPosDst);
								m_fDstRot = fRotDst;
								m_isGoing = true;
								__EnableSkipCollision();

								m_kMovAfterFunc.eFunc = eFunc;
								m_kMovAfterFunc.uArg = uArg;

								if (!IsWalking())
								{
									StartWalking();
								}
							}
							else
							{
								m_isGoing = false;

								if (IsWalking())
								{
									EndWalking();
								}

								SCRIPT_SetPixelPosition(kPPosDst.x, kPPosDst.y);
								SetAdvancingRotation(fRotDst);
								SetRotation(fRotDst);

								NEW_UseSkill(0, eFunc & 0x7f, uArg & 0x0f, (uArg >> 4) != 0 ? true : false);
							}
						}
						break;
					}
				}
			}
		}

