		protected uint __GetRefinedEffect(CItemData pItem)
		{
			uint refine = Math.Max(pItem.GetRefine() + pItem.GetSocketCount(),CItemData.ITEM_SOCKET_MAX_NUM) - CItemData.ITEM_SOCKET_MAX_NUM;
			uint vnum = pItem.GetIndex();

			switch (pItem.GetType())
			{
			case CItemData.ITEM_TYPE_WEAPON:
				__ClearWeaponRefineEffect();

				if (__IsTHWeapon(vnum))
				{
					switch (pItem.GetSubType())
					{
						case CItemData.WEAPON_DAGGER:
							m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SWORD_REFINED7TH_SMALL;
							m_swordRefineEffectLeft = EFFECT_REFINED + EFFECT_SWORD_REFINED7TH_SMALL_LEFT;
							break;
						case CItemData.WEAPON_FAN:
							m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SWORD_REFINED7TH_FAN;
							break;
						case CItemData.WEAPON_ARROW:
						case CItemData.WEAPON_QUIVER:
						case CItemData.WEAPON_BELL:
							m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SWORD_REFINED7TH_SMALLSWORD;
							break;
						case CItemData.WEAPON_BOW:
							m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SWORD_REFINED7TH_BOW;
							break;
#if ENABLE_WOLFMAN
//# Laniatus Games Studio Inc. | TODO TASK: C# does not allow fall-through from a non-empty 'case':
						case CItemData.WEAPON_CLAW:
							m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SWORD_REFINED7TH_CLAW;
							m_swordRefineEffectLeft = EFFECT_REFINED + EFFECT_SWORD_REFINED7TH_CLAW_LEFT;
							break;
#endif
//# Laniatus Games Studio Inc. | TODO TASK: C# does not allow fall-through from a non-empty 'case':
						default:
							m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SWORD_REFINED7TH_SWORD;
							break;
					}

					if (m_swordRefineEffectRight)
					{
						m_swordRefineEffectRight = __AttachEffect(m_swordRefineEffectRight);
					}
					if (m_swordRefineEffectLeft)
					{
						m_swordRefineEffectLeft = __AttachEffect(m_swordRefineEffectLeft);
					}
					return 0;
				}

				if (refine < 7)
				{
					return 0;
				}

				switch (pItem.GetSubType())
				{
				case CItemData.WEAPON_DAGGER:
					m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SMALLSWORD_REFINED7 + refine - 7;
					m_swordRefineEffectLeft = EFFECT_REFINED + EFFECT_SMALLSWORD_REFINED7_LEFT + refine - 7;
					break;
				case CItemData.WEAPON_FAN:
					m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_FANBELL_REFINED7 + refine - 7;
					break;
				case CItemData.WEAPON_ARROW:
				case CItemData.WEAPON_QUIVER:
				case CItemData.WEAPON_BELL:
					m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SMALLSWORD_REFINED7 + refine - 7;
					break;
				case CItemData.WEAPON_BOW:
					m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_BOW_REFINED7 + refine - 7;
					break;
#if ENABLE_WOLFMAN
//# Laniatus Games Studio Inc. | TODO TASK: C# does not allow fall-through from a non-empty 'case':
				case CItemData.WEAPON_CLAW:
					m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_CLAW_REFINED7 + refine - 7;
					m_swordRefineEffectLeft = EFFECT_REFINED + EFFECT_CLAW_REFINED7_LEFT + refine - 7;
					break;
#endif
//# Laniatus Games Studio Inc. | TODO TASK: C# does not allow fall-through from a non-empty 'case':
				default:
					m_swordRefineEffectRight = EFFECT_REFINED + EFFECT_SWORD_REFINED7 + refine - 7;
					break;
				}
				if (m_swordRefineEffectRight)
				{
					m_swordRefineEffectRight = __AttachEffect(m_swordRefineEffectRight);
				}
				if (m_swordRefineEffectLeft)
				{
					m_swordRefineEffectLeft = __AttachEffect(m_swordRefineEffectLeft);
				}
				break;

			case CItemData.ITEM_TYPE_ARMOR:
				__ClearArmorRefineEffect();

				if (pItem.GetSubType() == CItemData.ARMOR_BODY)
				{
					if (12010 <= vnum && vnum <= 12049 || 21080 <= vnum && vnum <= 21089)
					{
						__AttachEffect(EFFECT_REFINED + EFFECT_BODYARMOR_SPECIAL);
						__AttachEffect(EFFECT_REFINED + EFFECT_BODYARMOR_SPECIAL2);
					}

					if (12050 <= vnum && vnum <= 12099)
					{
						__AttachEffect(EFFECT_REFINED + EFFECT_BODYARMOR_SPECIAL3);
					}

					if (21220 <= vnum && vnum <= 21225 || 21240 <= vnum && vnum <= 21245 || 21260 <= vnum && vnum <= 21265 || 21280 <= vnum && vnum <= 21285 || 21300 <= vnum && vnum <= 21305)
					{
						__AttachEffect(EFFECT_REFINED + EFFECT_BODYARMOR_SPECIAL4);
					}
				}

				if (refine < 7)
				{
					return 0;
				}

				if (pItem.GetSubType() == CItemData.ARMOR_BODY)
				{
					m_armorRefineEffect = EFFECT_REFINED + EFFECT_BODYARMOR_REFINED7 + refine-7;
					__AttachEffect(m_armorRefineEffect);
				}
				break;

			case CItemData.ITEM_TYPE_COSTUME:
				if (pItem.GetSubType() == CItemData.COSTUME_WEAPON)
				{
					__ClearWeaponRefineEffect();
				}
				break;
			}
			return 0;
		}

